function set_price(t,e){var i=e.item.id;$.ajax({url:"/purchase_item/last/"+i,success:assign_price})}function assign_price(t){$("#purchase_item_quantity").val(t.quantity),$("#purchase_item_unit").val(t.unit),$("#purchase_item_price").val(t.price)}function set_delivery_details(t,e){var i=e.item.id;$.ajax({url:"/purchase_item/last/"+i,success:function(t){$("#item_delivery_unit").val(t.unit),$("#item_delivery_quantity").val(t.quantity)}})}function update_item(){var t=$(this).closest("tr").attr("id"),e=parseInt(t.match(/\d+/),10);$.ajax({dataType:"script",url:"/purchase_orders/update_purchase_items/"+e})}