function set_price(i,e){var t=e.item.id;$.ajax({url:"/purchase_item/last/"+t,success:assign_price})}function assign_price(i){$("#purchase_item_quantity").val(i.quantity),$("#purchase_item_unit").val(i.unit),$("#purchase_item_price").val(i.price)}function set_delivery_details(i,e){var t=e.item.id;$.ajax({url:"/purchase_item/last/"+t,success:function(i){$("#item_delivery_unit").val(i.unit),$("#item_delivery_quantity").val(i.quantity)}})}