function init_purchase_order_show_purchase_items_section(){$("#purchase_item_item_name").autocomplete({source:$("#purchase_item_item_name").data("autocomplete-source"),minLength:2,select:set_price}),$("#hide_new_purchase_item_link, #show_new_purchase_item_link").on("click",toggle_new_purchase_item_row_form),$("#purchase_item_quantity, #purchase_item_price").on("focusout",set_new_purchase_item_total),$("#new_purchase_item").on("submit",addingNewPurchaseItem),$("#purchase_item_table a.remove_fields").add_pending_deletion_class_to_closes_tr_on_click(),$(".items_section .best_in_place").bind("ajax:success",recalculate_total)}function recalculate_total(){}function toggle_new_purchase_item_row_form(e){$("#show_new_purchase_item_link_row").toggle(),$("#new_purchase_item_row").toggle(),$("#purchase_item_item_name").focus(),e.preventDefault()}function set_new_purchase_item_total(){qty=$("#purchase_item_quantity").val(),price=$("#purchase_item_price").val(),$("#purchase_item_total").val(qty*price),$("#purchase_item_total").number(!0,2)}function addingNewPurchaseItem(){var e=$("#purchase_item_item_name").val();e+=" x "+$("#purchase_item_quantity").val(),e+=" "+$("#purchase_item_unit").val(),$("#new_purchase_item_row").before("<tr class='adding_purchase_item'><td colspan='7'>Saving Purchase Item: "+e+"...</td>")}